import { observable, makeObservable, action } from 'mobx'

class BagState {
    items = []

    addItem = (item) => {
        this.items = [...this.items, item]
    }

    removeItem = (item) => {
        this.items = items.filter(item => item)
    }

    constructor() {
        makeObservable(this, {
            items: observable,
            addItem: action,
        })
    }
}

export default new BagState()
