import { observable, makeObservable, action } from 'mobx'

class BagState {
    items = []

    addItem = () => {
        this.items.push({ name: 'item' })
    }

    constructor() {
        makeObservable(this, {
            items: observable,
            addItem: action,
        })
    }
}

export default new BagState()
