const { observable, makeObservable, action } = 'mobx'

class BagState {
    items = []

    constructor() {
        makeObservable(this, {
            items: observable,
            addItem: action,
        })
    }

    addItem() {
        this.items.push({ name: 'item' })
    }
}

export default new BagState()
