import { observable, makeObservable, action, computed } from 'mobx'
import {
    persistence,
    useClear,
    useDisposers,
    isSynchronized,
    StorageAdapter,
} from 'mobx-persist-store'

function readStore(name) {
    return new Promise(resolve => {
        const data = localStorage.getItem(name)
        resolve(JSON.parse(data))
    })
}

function writeStore(name, content) {
    return new Promise(resolve => {
        localStorage.setItem(name, JSON.stringify(content))
        resolve()
    })
}

class BagState {
    items = []

    addItem = newItem => {
        const currentItem = this.items.find(item => item.name === newItem.name)
        // items.forEach(item => {
        //     item.name === newItem.name
        // });
        if (currentItem) {
            currentItem.quantity++
        } else {
            newItem.quantity = 1
            this.items = [...this.items, newItem]
        }
    }

    removeItem = itemName => {
        this.items = this.items.filter(item => item.name !== itemName)
    }

    changeQuantity = (itemName, value) => {
        const currentItem = this.items.find(item => item.name === itemName)
        currentItem.quantity = value
    }

    clearStore = () => {
        useClear(this)
    }

    persistDispose = () => {
        useDisposers(this)
    }

    get isSynchronized() {
        return isSynchronized(this)
    }

    constructor() {
        makeObservable(this, {
            items: observable,
            addItem: action,
            removeItem: action,
            changeQuantity: action,
            clearStore: action,
            persistDispose: action,
            isSynchronized: computed,
        })
    }
}

persistence({
    name: 'BagState',
    properties: ['items'],
    adapter: new StorageAdapter({
        read: readStore,
        write: writeStore,
    }),
})(BagState)

export default new BagState()
