import { observable, makeObservable, action } from 'mobx'

class BagState {
    items = []

    addItem = newItem => {
        items.forEach(item => {
            item.name === newItem.name
        });
        this.items = [...this.items, newItem]
    }

    removeItem = itemName => {
        this.items = this.items.filter(item => item.name !== itemName)
    }

    constructor() {
        makeObservable(this, {
            items: observable,
            addItem: action,
            removeItem: action
        })
    }
}

export default new BagState()
