import { useEffect, useState } from 'react'
import { observer } from 'mobx-react-lite'

import del from '../public/delete.svg'

const BagItem = ({ item, bag }) => {
    const { API_URL } = process.env
    const { removeItem, changeQuantity } = bag

    const [fetchedItem, setItem] = useState([])

    useEffect(async () => {
        await fetch(`${API_URL}/coffees/${item.id}`).then(res => setItem(res.json()))
    }, [])

    function handleChange(itemName, e) {
        console.log(e.target.value)
        if (e.target.value >= 0) {
            changeQuantity(itemName, e.target.value)
        }
    }

    return (
        <li className="bagItem" key={item}>
            <img className="photo" src={fetchedItem.image && fetchedItem.image.url} alt="" />
            <span className="title">{fetchedItem.name}</span>
            <span>${fetchedItem.price}</span>
            <input
                type="number"
                value={item.quantity}
                onChange={e => handleChange(fetchedItem.name, e)}
                min="0"
            />
            {/* <span>${(item.price * item.quantity).toFixed(2)}</span> */}
            <button onClick={() => removeItem(item.name)}>
                <img src={del} alt="delete" />
            </button>
            {console.log(fetchedItem)}
        </li>
    )
}

export default observer(BagItem)
